(7, 4, '', '26.gif', 1),
(8, 23, 'Phòng ngủ', '1488869870_16508918_1235610313161440_1006287174270635622_n.jpg', 1),
(9, 23, 'Phòng khách', '1488869887_16712029_1235610286494776_4103877826199629899_n.jpg', 1),
(10, 24, '131231', '1488870562_13.gif', 1),
(11, 24, '1231231231', '1488870570_29.gif', 1),
(12, 31, 'Không gian nhà ', '1489121594_3.jpg', 0),
(13, 31, 'Phòng khách', '1489397450_16003206_1199443440111461_8495381234877497951_n.jpg', 1),
(14, 31, 'Phòng ngủ', '1489397439_5.jpg', 1),
(15, 25, 'Sân Gold', '1489397344_1368023919dulich1.jpg', 1),
(16, 25, 'Nhà khách', '1489397358_5.jpg', 1),
(17, 25, 'View', '1489397371_23.gif', 1),
(18, 33, 'Mặt đường', '1489397489_26.gif', 1),
(19, 33, 'Tầm nhìn trên cao', '1489397513_22.gif', 1),
(20, 25, 'Sắc xanh ', '1493794690_Interior-Rumah-Minimalis-Cat-Warna-Hijau-4.jpg', 1),
(21, 45, 'Phòng khách trông đầy sức sống nhờ cách bố trí tinh tế hết hợp với các đồ nội thất đầy màu sắc. Khung cửa kính lớn giúp thu hút ánh sáng tự nhiên nên không gian luôn tạo cảm giác thông thoáng dù ở bất kì thời điểm nào trong ngày. Những gam màu trung tính ', '1493795244_căn hộ.jpg', 1),
(22, 45, 'Ghế sofa được đặt sát tường, để cô gái có thể thoải mái ngồi trên ghế và ngắm nhìn thiên nhiên qua khung cửa sổ.', '1493795232_hang-trieu-nguoi-se-phai-me-man-khi-ngam-nhin-ngoi-nha-cap-4-tren-dinh-nui-nay-2.jpg', 1),
(23, 45, 'Khu vực ăn uống cũng mang nét cổ điển nhưng vẫn giữ được nét sang trọng, sạch sẽ. Cách bố trí ngăn nắp, gọn gàng cũng là điểm cộng giúp không gian này chiếm được cảm tình của những ai ngắm nhìn. ', '1493795274_hang-trieu-nguoi-se-phai-me-man-khi-ngam-nhin-ngoi-nha-cap-4-tren-dinh-nui-nay-3.jpg', 1),
(24, 45, 'Ngay khu cửa ra vào cũng ngập tràn ánh sáng và mãn nhãn với cảnh vật thiên nhiên bên ngoài', '1493795334_hang-trieu-nguoi-se-phai-me-man-khi-ngam-nhin-ngoi-nha-cap-4-tren-dinh-nui-nay-6.jpg', 1),
(25, 45, 'Phòng ngủ sử dụng gam màu nữ tính như hồng tím, hồng, be… được kết hợp hài hòa mang lại cho không gian nghỉ ngơi vẻ đẹp yên bình, lãng mạn.', '1493795350_hang-trieu-nguoi-se-phai-me-man-khi-ngam-nhin-ngoi-nha-cap-4-tren-dinh-nui-nay-7.jpg', 1),
(26, 45, 'Khu vực tắm táp của mọi người trong gia đình được bố trí phía sau của ngôi nhà, đơn giản đến từng đường nét cũng như chi tiết nội thất.', '1493795376_hang-trieu-nguoi-se-phai-me-man-khi-ngam-nhin-ngoi-nha-cap-4-tren-dinh-nui-nay-9-682x1024.jpg', 1),
(27, 46, 'Một con tàu màu xanh đen nhấp nhô bao gồm những phòng họp, khu vực dịch vụ và phòng làm việc cá nhân. Phòng họp với những bức tường và cột nhà dành cho nhân viên có thể viết hoặc vẽ lên.', '1493795971_8_Bean_Buro_Workplace_UBER.jpg', 1),
(28, 46, 'Bộ phận lễ tân bao gồm những bức tường và sàn nhà được lợp những ô gạch với màu sắc sặc sỡ – đối lập với những chiếc đèn neon thủ công, trong khi bàn lễ tân được bọc nỉ gợi lên hình ảnh của chiếc ghế trong xe hơi sang trọng.', '1493796000_5_Bean_Buro_Workplace_UBER.jpg', 1),
(29, 46, 'Những cụm làm việc với không gian mở, có thể tiếp cận để hỗ trợ và kết nối với những khu vực khác Những khu vực riêng trên tàu bao gồm những không gian làm việc riêng biệt được ngăn bằng những tấm kính mờ.', '1493796037_4_Bean_Buro_Workplace_UBER.jpg', 1),
(30, 46, 'Thư viện', '1493796544_3_Bean_Buro_Workplace_UBER.jpg', 1),
(31, 46, 'Tất cả đều mang hơi hướng hiện đại và thông thoáng ', '1493796603_1_Bean_Buro_Workplace_UBER.jpg', 1),
(32, 46, 'Một môi trường làm việc văn minh, hiện đại sẽ giúp người làm việc có tinh thần hưng phấn, tạo hiệu quả cao trong công việc ', '1493796842_0_Bean_Buro_Workplace_UBER.jpg', 1),
(33, 49, 'Sắp xếp những vật dụng “lộn xộn” thành một bộ sưu tập thống nhất mang một ý nghĩa đặc biệt', '1493880725_hinh-2cafeland-1492395495.jpg', 1),
(34, 49, 'Nhà đẹp với phong cách nội thất tối đa maximalism', '1493880747_hinh-3cafeland-1492395499.jpg', 1),
(35, 49, 'Sử dụng những gam màu tươi sáng, bùng nổ là một vũ khí bí mật trong chủ nghĩa tối đa', '1493880786_hinh-4cafeland-1492395501.jpg', 1),
(36, 49, 'Nhà đẹp với phong cách nội thất tối đa maximalism', '1493880810_hinh-5cafeland-1492395505.jpg', 1),
(37, 49, 'Sử dụng rèm có họa tiết, thảm trải sàn, ghế sofa phải được kết hợp tự do, tư nhiên nhất nhưng hài hòa', '1493880844_hinh-6cafeland-1492395528.jpg', 1),
(38, 49, 'Nhà đẹp với phong cách nội thất tối đa maximalism', '1493880869_hinh-7cafeland-1492395536.jpg', 1),
(39, 49, 'Cả hình ảnh và tác phẩm nghệ thuật nói lên dấu ấn cá nhân, điều này quan trọng trong phong cách nội thất tối đa', '1493880949_hinh-8cafeland-1492395539.jpg', 1),
(40, 49, 'Treo những tấm ảnh hay bức tranh nghệ thuật ấn tượng bạn yêu thích cho không gian này', '1493880981_hinh-9cafeland-1492395542.jpg', 1),
(41, 49, 'Maximalism chính là sự kết hợp đa dạng và hoàn hảo giữa nhiều yếu tố văn hóa, nghệ thuật', '1493881008_hinh-10cafeland-1492395533.jpg', 1),
(42, 49, 'Nhà đẹp với phong cách nội thất tối đa maximalism', '1493881037_hinh-11cafeland-1492395545.jpg', 1),
(43, 52, 'Ngôi nhà không chỉ có diện tích nhỏ mà còn sở hữu hình dáng không mấy vuông vắn, 9 góc và các cạnh theo hình zig zắc, hơn nữa nó lại nằm trong khu dân cư tự phát với nhiều hạn chế về quy chế quản lý đô thị như số tầng, chiều cao cho phép xây dựng.', '1493890742_W.jpg', 1),
(44, 52, 'Mong muốn của chủ nhà là muốn xây ngôi nhà 1 lửng 2 lầu vừa để ở vừa cho thuê tăng thêm thu nhập, đảm bảo cuộc sống lâu dài nên mức đầu tư cũng hạn chế.', '1493890776_E.jpg', 1),
(45, 52, 'Vì thế sau khi xem xét cẩn trọng, kiến trúc sư cho rằng phải sử dụng giải pháp của Le Corbusier, mô hình của kiến trúc sư lỗi lạc người Pháp, đó là những vẻ đẹp, sự sắp xếp lớp lang, sự khác biệt trong tính thống nhất của kiến trúc đô thị.', '1493890820_R.jpg', 1),
(46, 52, 'Ngôi nhà chia thành 2 phần theo phương thẳng, phần thấp bao gồm tầng trệt và tầng lửng, trong đó tầng trệt có diện tích xây dựng khoảng 24m2, với 3m2 sân vườn phía trước dành cho tiệm nước. Tầng lửng được thiết kế để cho thuê sau đó thành phòng cho trẻ ở.', '1493890877_T.jpg', 1),
(47, 52, 'Phần trên là nơi sinh hoạt của gia đình, có lối riêng tạo thành căn hộ trên cao với 50% diện tích xây dựng cho bếp và không gian ăn uống, phòng khách mở rộng với mái kính cường lực trượt chữ L, diện tích còn lại là phòng ngủ và phòng vệ sinh.', '1493890907_Y.jpg', 1),
(48, 52, 'Cầu thang được thiết kế khác biệt, tạo cảm giác hài hòa cho căn nhà.', '1493890972_U.jpg', 1),
(49, 52, 'Mỗi nơi đều có thiết kế thanh lịch, hiện đại mang đến nhiều cảm hứng cho gia chủ.', '1493891114_i.jpg', 1),
(50, 52, 'Cách bố trí hợp lý mang đến cho ngôi nhà một không gian ngập tràn ánh sáng. ', '1493891188_o.jpg', 1),
(51, 54, 'Một thác nước phía trước căn nhà được tạo thành từ những hòn sỏi. Nếu có thể, bạn hãy thể thiết kế thêm hệ thống ánh sáng, phun nước để có một nơi đẹp lung linh về đêm ngay trong khuôn viên căn nhà.', '1493953381_c.jpg', 1),
(52, 54, 'Trang trí thêm cho phía bên ngoài cửa sổ bằng những cụm hoa sắc màu.', '1493953418_v.jpg', 1),
(53, 54, 'Sử dụng gạch tạo thành một bồn hoa trang trí thêm cho phía bên ngoài, tạo sự sạch sẽ và thêm màu sắc cho căn nhà.', '1493953458_b.jpg', 1),
(54, 54, 'Nơi hiên nhà, bạn có thể trang trí theo mùa hay tùy ý theo chủ đề mà mình thích.', '1493953499_mat-tien-5-1479833241.jpg', 1),
(55, 54, 'Đặt một chậu cây bằng gỗ nổi bật trước nhà.', '1493953533_mat-tien-7-1479833295.jpg', 1),
(56, 54, 'Những chậu hoa đặc biệt đặt trước cửa nhà.', '1493953566_mat-tien-10-1479833347.jpg', 1),
(57, 54, 'Dọc lối dẫn vào nhà, bạn có thể đặt những giỏ hoa trên cọc gỗ cao, trông đơn giản nhưng không kém phần trang nhã.', '1493953598_mat-tien-8-1479833313.jpg', 1),
(58, 54, 'Một cửa có thiết kế ấn tượng, thu hút ngay từ cái nhìn đầu tiên, bạn có thể trang trí thêm bằng cách đặt những chậu hoa hai bên.', '1493953629_mat-tien-11-1479833364.jpg', 1),
(59, 54, 'Một lối vào cũng rất ấn tượng khi được điểm tô bằng những chậu hoa màu trắng, chậu hoa và quả bí ngô được tô cùng tông màu với bậc thang, tạo cái nhìn đơn giản nhưng sạch sẽ.', '1493953656_mat-tien-12-1479833384.jpg', 1),
(60, 54, 'kkjjj', '1498016045_Capture001.png', 1);
